import React from 'react'
import { Formik, Form } from "formik";
import { FaEdit } from 'react-icons/fa';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import BCustomTextInput from '../utilities/BCustomTextInput';
import BCustomNumberInput from '../utilities/BCustomNumberInput';
import EducationService from '../services/EducationService'
import Button from '@material-ui/core/Button';
import * as Yup from "yup";

import {
    Card, Icon, Image, Container, GridColumn, Grid, Label,
} from 'semantic-ui-react'
export default function AddEducation() {

    const [open, setOpen] = React.useState(false);



    const initialValues = { graduteYear: "", schoolName: "", id:1 }


    const schema = Yup.object({
        graduteYear: Yup.number().required("Work place name is required"),
        schoolName: Yup.string().required("Work position name is required"),
         
    })


    const handleClickOpen = () => {
        setOpen(true);
    }

    const handleClose = () => {
        setOpen(false);
    };

    function addEducation(values) {
        let educationService  = new EducationService()
        educationService.add(values)
    
    }

    return (
        <div>
            <a onClick={handleClickOpen}><FaEdit size='30' style={{ float: 'right', margin: '10px', color: "white" }}></FaEdit></a>

            <Dialog fullWidth="1000px" open={open} onClose={handleClose} aria-labelledby="form-dialog-title">

                <DialogTitle id="form-dialog-title">Add Education</DialogTitle>
                <DialogContent>
                    <Formik initialValues={initialValues}
                        validationSchema={schema}
                        onSubmit={(values) => {
                            addEducation(values)
                        }}
                    >
                        <Form style={{ marginTop: "15px" }} className="ui form">
                        <Label content="Graduate Year" style={{ marginBottom: "5px", marginTop: "5px", float: "left" }}></Label>

                            <BCustomNumberInput
                                name="graduteYear"
                                placeholder="Graduate Year"
                            />
                            <Label content="School Name" style={{ marginBottom: "5px", marginTop: "5px", float: "left" }}></Label>

                            <BCustomTextInput
                                name="schoolName"
                                placeholder="School Name"
                            />
                 
                            <DialogActions>
                                <Button onClick={handleClose} color="primary">
                                    Cancel
                                </Button>
                                <Button type="submit" color="primary">
                                    Add
                                </Button>
                            </DialogActions>
                        </Form>
                    </Formik>

                </DialogContent>

            </Dialog>

        </div>
    )
}
