import React, { Component, useState } from 'react'
import {
    Button,
    Checkbox,
    Form,
    Input,
    Radio,
    Select,
    TextArea,
} from 'semantic-ui-react'
import {
    DateInput,
    TimeInput,
    DateTimeInput,
    DatesRangeInput
} from 'semantic-ui-calendar-react';
export default function AddJobAdvertisements() {

    const options = [
        { key: 't', text: 'Turkey', value: 'Turkey' },
        { key: 'u', text: 'Us', value: 'Us' },
        { key: 'g', text: 'Germany', value: 'Germany' },
        { key: 'j', text: 'Japan', value: 'Japan' },
        { key: 'k', text: 'Uk', value: 'Uk' },

    ]
    const [date, setDate] = useState({
        date: '',
        time: '',
        dateTime: '',
        datesRange: ''
    })
    const [jobPosition, setJobPosition] = useState()



    var handleChange = (event, { name, value }) => {
        if (date.hasOwnProperty(name)) {
            setDate({ [name]: value });
        }
        if (jobPosition.hasOwnProperty(name)){
            setJobPosition({[name]: value});
            console.log(jobPosition)
        }
    }


    return (
        <div>
            <Form>
                <Form.Group widths='equal'>
                    <Form.Field
                        name="jobPosition"
                        control={Input}
                        label='Job Position'
                        placeholder='Job Position'
                        value={jobPosition}
                        onChange={handleChange}
                    />

                </Form.Group>
                <Form.Field
                    control={TextArea}
                    label='Job Description'
                    placeholder='Tell us more about job...'
                />
                <Form.Group widths='equal'>
                    <Form.Field
                        control={Input}
                        label='max salary'
                        placeholder='max salary'
                        type="number"
                    />
                    <Form.Field
                        control={Input}
                        label='min salary'
                        placeholder='min salary'
                        type="number"

                    />

                </Form.Group>
                <Form.Field>
                    <Form.Field
                        control={Select}
                        label='Location'
                        options={options}
                        placeholder='Gender'
                    />
                </Form.Field>
                <Form.Field>
                    <DateInput
                        name="date"
                        placeholder="Date"
                        value={date.date}
                        iconPosition="left"
                        onChange={handleChange}
                    />
                </Form.Field>

                <Form.Field
                    control={Checkbox}
                    label='I agree to the Terms and Conditions'
                />
                <Form.Field i semantic-ui-calendar-react
                    control={Button}>Submit</Form.Field>
            </Form>


        </div>
    )

}
