import React, { useState, useEffect } from 'react'
import { useParams } from "react-router";

import JobAdvertisementService from '../services/JobAdvertisementService'
export default function JobAdvertisementDetails() {

    let {jobAdvertisement_id} = useParams()

    const [jobAdvertisementDetail, setJobAdvertisementDetail] = useState(initialState)
    useEffect(()=> {
        let jobAdvertisementService = new JobAdvertisementService()
        jobAdvertisementService.getById(jobAdvertisement_id).then(result => setJobAdvertisementDetail(result.data.data))
    })
    return (
        <div>
            <h1>{jobAdvertisement_id}</h1>
            { jobAdvertisementDetail ? jobAdvertisementDetail.map((jobAdvertisement)=> (
                <h1>{jobAdvertisement.jobDescription}</h1>
            )) :null}

        </div>
    )
}
