import React, { useEffect,useState } from 'react'
import JobAdvertisementService from '../services/JobAdvertisementService'
import { Card, Icon, Image, Container, GridColumn, Grid } from 'semantic-ui-react'

export default function JobAdvertisement() {

    const [jobAdvertisements, setJobAdvertisement] = useState([]) 
    useEffect(()=> {
        let jobAdvertisementService = new JobAdvertisementService()
        jobAdvertisementService.getAllDetail().then(result => setJobAdvertisement(result.data.data))
    },[])

    return (
        <div>
            <Container>
            { jobAdvertisements ? jobAdvertisements.map((jobAdvertisement) =>(
                <div>{jobAdvertisements.companyName}</div>
            )):""}
            </Container>
     
        </div>
    )
}
