import { React, useEffect, useState } from 'react'
import { useParams } from "react-router";
import ApplicationService from '../services/ApplicationService'
import { Card, Icon } from 'semantic-ui-react'

export default function JobApplications() {

    let { jobAdvertisementId } = useParams()

    const [applications, setApplications] = useState([])


    useEffect(() => {
        let applicationService = new ApplicationService()
        applicationService.getApplicationByJobAdvertisementId(11).then(result => setApplications(result.data.data))
    }, [])

    return (
        <div>
            {applications ? applications.map((application) => (
                <Card
                    image="https://images.pexels.com/photos/220453/pexels-photo-220453.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"
                    header={application.jobSeeker.firstName + " " + application.jobSeeker.lastName}
                    meta={application.jobSeeker.dateOfBirth}
                    description={application.jobSeeker.email}
                />
            )) : null}

        </div>
    )
}
