import React, { Component, useState } from 'react'
import {
    Button,
    Checkbox,
    Form,
    Input,
    Radio,
    Select,
    TextArea,
} from 'semantic-ui-react'
import {
    DateInput,
    TimeInput,
    DateTimeInput,
    DatesRangeInput
} from 'semantic-ui-calendar-react';
import { Formik } from 'formik';
import * as Yup from "yup";
import BCustomTextInput from '../utilities/BCustomTextInput'
export default function JobAdvertisementAdd() {

    const initialValues = { jobPosition: "", jobDescription: "", maxSalary: 0, minSalary: 0 }

    const schema = Yup.object({
        jobPosition: Yup.string().required("Job position is required"),
        jobDescription: Yup.string().required("Job description is required"),
        maxSalary: Yup.number().required("Max salary is required"),
        minSalary: Yup.number().required("Min salary is required"),

    })


    return (
        <div>
            <Formik 
            initialValues={initialValues}
            validationSchema={schema}
            onSubmit = {(values)=>{
                console.log(values)
            }}
            >
                <Form className="ui form">
                <BCustomTextInput name="jobPosition" placeholder="Job Position" />
                <BCustomTextInput name="jobDescription" placeholder="Job Description" />

                </Form>
            </Formik>
        </div>
    )

}
